--
-- Structure de la table `Tintins`
--

CREATE TABLE `Tintins` (
  `id` int(11) NOT NULL,
  `title` varchar(255) DEFAULT NULL,
  `picture` varchar(255) DEFAULT NULL,
  `synopsis` text,
  `movie` varchar(255) DEFAULT NULL,
  `rating` int(11) DEFAULT NULL,
  `slug` varchar(255) NOT NULL,
  `creation_times_tamp` datetime NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Déchargement des données de la table `Tintins`
--

INSERT INTO `Tintins` (`id`, `title`, `picture`, `synopsis`, `movie`, `rating`, `slug`, `creation_times_tamp`) VALUES
(0, 'Tintin en Amérique épisode 1', '1.jpg', 'Dans Tintin en Amérique (1932), le héros confirme sa vocation de redresseur de torts, en s\'opposant au mafioso Al Capone, aux gangsters de Chicago et aux fripouilles de tout acabit. Déjà, Hergé témoigne d\'une vision généreuse du monde, stigmatisant par exemple des Blancs pour leurs comportements envers les Indiens Peaux-Rouges. La renommée de Tintin s\'étend au-delà de l\'Atlantique. Si bien que lorsqu\'il arrive à Chicago, en pleine prohibition, tous les bandits et malfaiteurs associés lui préparent une réception des moins confortables. Tintin devra user de tout son courage et de toute son intelligence pour survivre !', 'https://www.youtube.com/watch?v=cyxNiV-TAzk&list=PL4TMSDjRUYOnLSvWwRIT8Uz2EiUSSbFo9', 3, 'tintin-en-amerique', '2022-11-14 17:04:27'),
(1, 'Tintin en Amérique épisode 2', '2.jpg', 'Dans Tintin en Amérique (1932), le héros confirme sa vocation de redresseur de torts, en s\'opposant au mafioso Al Capone, aux gangsters de Chicago et aux fripouilles de tout acabit. Déjà, Hergé témoigne d\'une vision généreuse du monde, stigmatisant par exemple des Blancs pour leurs comportements envers les Indiens Peaux-Rouges. La renommée de Tintin s\'étend au-delà de l\'Atlantique. Si bien que lorsqu\'il arrive à Chicago, en pleine prohibition, tous les bandits et malfaiteurs associés lui préparent une réception des moins confortables. Tintin devra user de tout son courage et de toute son intelligence pour survivre !', 'https://www.youtube.com/watch?v=k58EMlREtNw', 2, 'tintin-en-amerique', '2022-11-14 17:15:38'),
(2, 'L\'île Noire', '6.jpg', 'De retour d\'Amérique du Sud, Tintin s\'embarque dans une aventure britannique palpitante, rythmée par d\'incessants rebondissements. Pour la première fois et non la dernière, Tintin s\'oppose au fourbe Docteur Müller. Ce dernier a organisé, à l\'échelle européenne, un vaste trafic de fausse monnaie. Après bien des péripéties, Tintin réussira-t-il à le démanteler ?', 'https://www.youtube.com/watch?v=4CkKDFxLlT4', 4, 'l-ile-noire', '2022-07-01 12:35:38'),
(3, 'Le Spectre d\'Ottokar', '7.jpg', 'Le Sceptre d\'Ottokar (1939) est un coup de force d\'Hergé qui vise l\'Allemagne dans cette aventure. La découverte d\'une serviette oubliée sur un banc conduit Tintin à Prague, puis en Syldavie. Tintin va sauver la Syldavie et son Royaume d\'un coup d\'État fasciste.', 'https://www.youtube.com/watch?v=uLCGbWXvyOE', 3, 'le-spectre-d-ottokar', '2022-07-01 12:35:38'),
(4, 'Les Cigares du Pharaon', '3.jpg', 'Ce quatrième épisode de la série, premier album paru exclusivement chez Casterman (les précédents étaient estampillés Éditions du Petit Vingtième) sous le titre Les aventures de Tintin reporter en Orient, Les Cigares du Pharaon, est sorti à l\'automne 1934. Tintin part sur les traces de trafiquants d\'opium à travers l\'Égypte et l\'Inde. Port-Saïd, Le Caire, les pyramides, les tombeaux des pharaons, la mer Rouge, la jungle et ses éléphants... Tintin va vivre des aventures peu banales : il tente d\'échapper au poison qui rend fou, rencontre un archéologue extravagant, un Maharadja en danger de mort...', 'https://www.youtube.com/watch?v=NyGc1P4zp_E', 3, 'les-cigares-du-pharaon', '2022-07-01 12:35:38'),
(5, 'L\'oreille cassée', '5.jpg', 'L\'Oreille Cassée (1937) est une course poursuite palpitante. Tintin s\'embarque pour l\'Amérique du Sud afin de récupérer un fétiche volé. Là-bas s\'opposent toutes sortes d\'intérêts : militaires, économiques, la guerre du Gran Chaco venant d\'opposer, trois ans durant, la Bolivie et le Paraguay. Une statuette Arumbaya est volée... puis restituée à son musée. Mais un détail révèle à Tintin que ce n\'est pas l\'original qui a été rendu, mais une simple réplique. Quel mystère cache donc cette statuette pour que l\'on veuille en maquiller le vol ? Tintin s\'embarque pour l\'Amérique du Sud où, croit-il, se trouve la clef de cette énigme.', 'https://www.youtube.com/watch?v=VeAkTmt8zSM', 3, 'l-oreille-cassée', '2023-01-23 19:22:53'),
(6, 'Le Lotus Bleu', '4.jpg', 'Dans Le Lotus bleu (1936) Tintin s\'engage à démanteler le trafic d\'opium international qui sévit dans un pays mythique certes, mais dont il ignore tout, la Chine. À l\'aube de ce récit, la généreuse ambition de Tintin semble démesurée. Aidé par la seule société secrète, Les Fils du Dragon, et par son ami Tchang, rencontré tardivement, il parvient à surmonter tous les obstacles et ruiner les perfides machinations de ses nombreux ennemis.', 'https://www.youtube.com/watch?v=ZDbGDK-kTLk', 5, 'le-lotus-bleu', '2022-07-01 12:35:38'),
(7, 'Le Crabe aux Pinces d\'Or', '8.jpg', 'Le Crabe aux pinces d\'or (1941) renoue avec l\'aventure exotique. Celle-ci mène Tintin en Afrique du Nord. Il y déjoue les plans d\'une bande de malfaiteurs qui dissimulaient de l\'opium dans des boîtes de crabe. Avec l\'apparition du Capitaine Haddock, nous découvrons un nouvel art de s\'exprimer qui ne manque pas de piment et de sonorité. Parmi eux, l\'un des plus inattendus est à coup sûr végétarien.', 'https://www.youtube.com/watch?v=8949VJqisVE', 4, 'le-crabe-aux-pinces-d-or', '2022-07-01 12:35:38'),
(8, 'L\'Etoile mystérieuse', '9.jpg', 'À bord d\'un navire polaire dont le capitaine Haddock assure le commandement, Tintin et quelques savants européens partent pour l\'océan Arctique, où s\'est écrasé un aérolithe contenant un métal inconnu. Mais dès qu\'ils apprennent qu\'un autre navire fait également route vers l\'étrange météorite, leur expédition se transforme en une véritable course contre la montre.', 'https://www.youtube.com/watch?v=2am-B8Poiyc', 3, 'l-etoile-mysterieuse', '2022-07-01 12:35:38'),
(9, 'Le Secret de la Licorne', '10.jpg', 'En fouillant le grenier, le capitaine Haddock retrouve la trace de son ancêtre, le chevalier de Hadoque. Ce légendaire loup des mers s\'est rendu célèbre par ses déboires avec le cruel pirate Rackham le Rouge. Un roman maritime de plus ? Certainement pas ! Car le chevalier devient le dépositaire d\'un fabuleux trésor. Pour Tintin, Haddock et une série de malfrats, il s\'agit de le retrouver. Mais les pistes et les énigmes se multiplient.', 'https://www.youtube.com/watch?v=JpvK_uyMcpw', 4, 'le-secret-de-la-licorne', '2022-07-01 12:35:38'),
(10, 'Le Trésor de Rackham le Rouge', '11.jpg', 'Dans Le Secret de La Licorne (1943) et Le Trésor de Rackham le Rouge (1944) qui en est la suite, Tintin accompagne le capitaine Haddock sur les traces de son glorieux ancêtre, le chevalier François de Hadoque. Inventeur d\'un sous-marin de poche en forme de requin, un certain Tryphon Tournesol contribue à la découverte du trésor, avant d\'offrir au Capitaine le château de ses aïeux : Moulinsart.', 'https://www.youtube.com/watch?v=4hQt3EnY_Bk', 4, 'le-tresor-de-rackham-le-rouge', '2022-07-01 12:35:38'),
(11, 'Les 7 Boules de cristal', '12.jpg', 'Dans Les 7 Boules de cristal (1948), sept savants sont mystérieusement frappés de léthargie à leur retour d\'une expédition dans les Andes. Le professeur Tournesol ayant disparu, Tintin et le Capitaine partent à sa recherche. Créé en 1929 par un certain Georges Remi, qui signait d\'ores et déjà ses dessins du nom d\'Hergé, Tintin connaîtra vingt-trois aventures dont, jusqu\'à présent, le succès auprès des jeunes de 7 à 77 ans ne s\'est jamais démenti.', 'https://www.youtube.com/watch?v=quqHAY4ai64', 5, 'les-7-boules-de-cristal', '2022-07-01 12:35:38'),
(12, 'Le Temple du Soleil', '13.jpg', 'Dans Le Temple du Soleil (1949), Tintin, Milou et le Capitaine Haddock s\'envolent pour le Pérou à la recherche du professeur Tournesol, enlevé pour avoir involontairement commis un sacrilège qui le destine au châtiment suprême.', 'https://www.youtube.com/watch?v=M71CNZvQVgk', 4, 'le-temple-du-soleil', '2022-07-01 12:35:38'),
(13, 'Tintin au pays de l\'or noir', '14.jpg', 'La guerre a brutalement interrompu la prépublication de cette aventure, le 9 mai 1940. À cette époque, ni le capitaine Haddock, ni le professeur Tournesol, ni le château de Moulinsart n\'avaient rejoint la saga tintinesque. Huit ans plus tard, le Journal Tintin publie à nouveau cet épisode. Avec un humour génial, Hergé va réintégrer les absents de jadis dans la refonte de l\'aventure qu\'il entreprend d\'achever. En 1950, l\'album est publié. Mais le monde change, aussi en conformité avec l\'actualité, une nouvelle version, quelque peu modifiée, sera proposée au public en 1971.', 'https://www.youtube.com/watch?v=t5kFIdjlu78', 5, 'tintin-au-pays-de-l-or-noir', '2022-07-01 12:35:38'),
(14, 'Objectif Lune', '15.jpg', 'Dans Objectif Lune (1953), le Professeur Tournesol invite Tintin et le Capitaine Haddock à le rejoindre en Syldavie où il travaille sur le plus grand projet du siècle : l\'envoi d\'une fusée sur la Lune. Le défi s\'annonce palpitant, d\'autant plus qu\'au grand effarement du Capitaine, le Professeur leur suggère d\'être du voyage !', 'https://www.youtube.com/watch?v=GzZSP0YjNuQ', 5, 'objectif-lune', '2022-07-01 12:35:38'),
(15, 'On a marché sur la Lune', '16.jpg', 'Cet album, d\'une exactitude prophétique, poursuit l\'aventure lunaire initiée dans Objectif Lune. Hergé repousse sans cesse les limites scénaristiques et envoie cette fois ses héros dans l\'espace. Si aujourd\'hui aller dans l\'espace est presque une routine, au début des années cinquante, imaginer un tel récit relevait de la science-fiction. En effet, il est important de se rappeler que l\'album fut publié en 1954 alors qu\'Armstrong ne fît le premier pas sur la Lune qu\'en 1969.', 'https://www.youtube.com/watch?v=7zEqcCDW3T0', 5, 'on-a-marche-sur-la-lune', '2022-07-01 12:35:38'),
(16, 'L\'Affaire Tournesol', '17.jpg', 'Une sensationnelle invention du professeur Tournesol commence par provoquer des catastrophes au château de Moulinsart : toutes les vitres volent en éclat, ainsi que la plupart des objets en verre! Malheureusement, cette trouvaille ne rejoindra pas le rayon des farces et attrapes. Des espions tentent de s\'emparer de Tournesol pour lui soutirer ses plans. Il apparaît que les kidnappeurs sont des Bordures, éternels ennemis des Syldaves et bien décidés à transformer l\'invention de Tournesol en arme de destruction massive.', 'https://www.youtube.com/watch?v=d8tlK-_Dgmk', 3, 'l-affaire-tournesol', '2022-07-01 12:35:38'),
(17, 'Coke en stock', '18.jpg', 'Coke en stock met en lumière les pratiques scandaleuses des esclavagistes des temps modernes. L\'incrédulité du capitaine Haddock n\'y changera rien. Le trafic d\'êtres humains, cela existait encore au vingtième siècle. Au vingt-et-unième aussi, hélas. Comme pour bien d\'autres questions graves, Hergé fut sensible à cette dramatique actualité et la transposa dans une fiction pleine de rebondissements.', 'https://www.youtube.com/watch?v=CN7lXhe-ohg', 4, 'coke-en-stock', '2022-07-01 12:35:38'),
(18, 'Tintin au Tibet', '19.jpg', 'Tintin au Tibet (1960), pure histoire d\'amitié, sans le moindre méchant, décrit la recherche désespérée de Tintin pour retrouver son ami, le jeune Chinois Tchang, qui se rendait en Europe à bord d\'un avion de ligne qui s\'est écrasé dans l\'Himalaya. Ce récit pathétique, qui rompt avec le ton extraverti des épisodes précédents, démontre que la fidélité et l\'espoir sont capables de vaincre tous les obstacles, et que les préjugés - en l\'occurrence, à l\'égard de l\'abominable homme des neiges - sont bien souvent le fruit de l\'ignorance.', 'https://www.youtube.com/watch?v=um5PSWg9tRs', 4, 'tintin-au-tibet', '2022-07-01 12:35:38'),
(19, 'Les bijoux de la Castafiore', '20.jpg', 'Dans Les Bijoux de la Castafiore (1963), les principaux protagonistes de la série se retrouvent à Moulinsart pour y vivre une véritable comédie classique à huis clos. Tournant résolument le dos à l\'aventure pour s\'attacher à la difficulté de la communication entre les êtres, un \"anti-récit\" truffé de malentendus et de quiproquos plus cocasses les uns que les autres.', 'https://www.youtube.com/watch?v=-Ho7Iw4tR3A', 3, 'les-bijoux-de-la-castafiore', '2022-11-14 17:08:22'),
(20, 'Vol 714 pour Sydney', '21.jpg', 'Vol 714 pour Sydney (1968), c\'est le voyage interrompu, le détournement qui bouleverse tout, l\'incursion de Tintin et de ses compagnons dans l\'inconnu, dans un monde irréel animé par des phénomènes télépathiques, c\'est le contact incroyable avec des extra-terrestres et la sortie d\'un rêve... Mais en est-ce bien un ? Djakarta, dernière escale avant Sydney pour le Boeing du vol 714.', 'https://www.youtube.com/watch?v=ZVdtgEAJfBk', 4, 'vol-714-pour-sydney', '2022-07-01 12:35:38'),
(21, 'Tintin et les Picaros', '22.jpg', 'Dernier album achevé du maître dont l’écriture et la mise en forme auront pris plus de dix ans. L\'attente du lecteur se fait de plus en plus longue entre chaque nouvelle aventure. Sombre histoire de vengeance avec prise d’otages sur fond de guérilla, Tintin et les Picaros (1976) marque le retour de Tintin au San Theodoros, le pays de L’Oreille Cassée. Hergé y risque un constat doux amer, tendant à faire croire que tout en ce monde n’est que mascarade.', 'https://www.youtube.com/watch?v=PtDzKLR87S0', 3, 'tintin-et-les-picaros', '2022-07-01 12:35:38'),
(22, 'Tintin au Congo', '25.jpg', 'Les Aventures de Tintin, reporter du Petit Vingtième au Congo (1931) est le reflet d\'une époque coloniale et paternaliste. Pour ces nouvelles aventures, Hergé improvise encore le récit, mais plus pour longtemps. À peine revient-il d\'URSS que Tintin se rend en Afrique. Devenu sorcier au royaume de Babaoro\'m, Tintin déjouera les pièges d\'une bande de gangsters qui souhaitaient contrôler la production de diamant au Congo. L\'Afrique, représentée de manière naïve, reflète l\'esprit paternaliste d\'une Belgique colonialiste au début des années 1930.', 'https://www.youtube.com/watch?v=EDHBp5yWSZg', 5, 'tintin-au-congo', '2022-11-14 17:30:55'),
(23, 'Tintin au pays des Soviéts', '24.jpg', 'Nous sommes le 10 janvier 1929, à Bruxelles. Accompagné de son chien Milou, un tout jeune reporter monte dans le train à destination de Moscou. Pour Tintin, c\'est le début d\'une grande aventure. Pour Hergé, c\'est le vrai début de sa carrière. Les Aventures de Tintin, reporter du Petit Vingtième au pays des Soviets paraîtront sous forme d\'album en 1930. Cette année marque la naissance d\'un mythe qui n\'est pas près de s\'éteindre, et les premiers signes d\'une troublante confrontation entre la fiction et la réalité.', 'https://www.youtube.com/watch?v=1-ZovTqSJRA', 4, 'tintin-au-pays-des-soviets', '2022-07-01 12:35:38'),
(24, 'Tintin et le lac aux requins', '23.jpg', 'Le professeur Tournesol vient d\'inventer une sorte de photocopieur en trois dimensions, capable de reproduire n\'importe quel objet. Hélas, le sinistre Rastapopoulos, qui a pris pour pseudonyme Grand Requin, tente de s\'emparer de l\'appareil afin de reproduire frauduleusement des oeuvres d\'art volées dans des musées et stockées dans son repaire sous les eaux du lac Fléchizaff. Avec l\'aide de Niko et Nouchka, deux jeunes Syldaves, Tintin réussira à déjouer ses plans.', 'https://www.youtube.com/watch?v=OgbK2oEdLWk', 2, 'tintin-et-le-lac-aux-requins', '2022-11-14 17:34:13'),
(25, 'Tintin et le covid-19', '28.jpg', 'Tintin découvre la face caché de l\'épidémie. Avec les Duponts, le capitaine et son fidéle compagnon Milou il va tenter de clarifier cette machination politique mondiale.', 'https://www.youtube.com/watch?v=JUzglMitZjQ', 1, 'tintin-covid', '2022-11-10 17:22:37'),
(26, 'Tintin et la licorne', '27.jpg', 'Le jeune reporter aimé de tous, Tintin, et son fidèle chien, Milou - personnages célèbres créés par Hergé -, prennent vie dans le succès mondial, The Adventures of Tintin. Après avoir découvert une maquette de bateau contenant un secret explosif, Tintin et ses amis se retrouvent sur le chemin d\'un méchant diabolique. De la haute mer jusqu\'aux déserts de l\'Afrique du Nord, chaque nouvel événement surprend le spectateur avec encore plus de frisson, de péril et d\'aventure pour toute la famille.', 'https://www.youtube.com/watch?v=UrqYEbYlLk8', 5, 'tintin-licorne', '2022-11-28 14:26:14'),
(11844, 'tintin au Pérou', 'tintin-lama.jpg', 'tintin et ses amis ne souhaite plus se lancer dans un nouveau périple à travers le monde et décide d\'occuper leur temps à l\'apprentissage de la musique.', 'https://www.youtube.com/watch?v=AA3Tsoyvw8s', 4, 'tintin-perou', '2022-11-14 17:30:55'),
(11851, 'zaeazezae', '640px-Pino-tour-2.jpg', 'qsdqsdsqdazeazeazeqsd', 'qsdqsdazeraze', 2, 'qsd-qsd', '2023-02-28 14:33:42');

